import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

function App() {
  const [newToDo, setNewTodo] = useState("");
  const todos = useSelector((state) => state.todos);
  const dispatch = useDispatch();

  const add_todo = () => {
    if (newToDo.trim()) {
      dispatch({ type: "ADD_TODO", title: "New todo" });
    }
    setNewTodo("");
  };

  const remove_todo = (index) => {
    dispatch({ type: "REMOVE_TODO", title: index });
  };

  return (
    <div>
      <h1>To Do List</h1>
      <input
        type="text"
        value={newToDo}
        onChange={(e) => setNewTodo(e.target.value)}
      />
      <button onClick={add_todo}>Add work</button>
      <ul>
        {todos.map((todo, index) => {
          return (
            <li key={index}>{}
              {todo}{" "}
              <button
                onClick={(e) => {
                  remove_todo(index);
                }}
              >
                Remove
              </button>
            </li>
          );
        })}
      </ul>
    </div>
  );
}
export default App;
