import Image from "./Image";
import Comments from "./Comments";


export default class Content extends Component {
  constructor(props) {
    super(props);
    this.state = {
      comments: null,
    };
  }

  async Getcomments() {
    const comment = await fetch(`http://localhost:3002/comments?post-id=${this.props.post.id}`);
    const data = await comments.json();
    console.log(this.props.post.id);

    return await data;
  }

  componentDidMount() {
    this.Getcomments().then((data) => {
      this.setState({ comments: data });
    });
  }

  componentDidUpdate(prevprops) {
    if(prevprops.post.id !== this.props.post.id) {
      this.Getcomments().then((data) => (this.setState({comments: data})))
    }
  }

  render() {
    return (
      <div className="container">
        <Image post={this.props.post}/>

        {null != this.state.comments && <Comments comments={this.state.comments} />}
      </div>
    );
  }
}
