import { createSlice } from "@reduxjs/toolkit";

const taskSlice = createSlice({
  name: "tasks",
  initialState: { tasks: [] },
  reducers: {
    addTask: (state, action) => {
      state.tasks.push({
        id: Date.now(),
        title: action.payload,
        status: "To Do",
      });
    },
    removeTask: (state, action) => {
      state.tasks = state.tasks.filter((task) => task.id !== action.payload.id);
    },
    upadeStatus: (state, action) => {
      const task = state.tasks.find((task) => task.id === task.payload.id);
      if (task) {
        task.status = action.payload.status;
      }else {
        console.error('Task not found:', action.payload.id); // نمایش خطا در صورت پیدا نشدن تسک
      }
    },
  },
});

export const { addTask, removeTask, upadeStatus } = taskSlice.actions;
export default taskSlice.reducer;
